import React, { useState, useEffect } from "react";

const TypingText = ({ text = "", speed = 150, pause = 1000 }) => {
  const [displayedText, setDisplayedText] = useState("");
  const [index, setIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  useEffect(() => {
    const timeout = setTimeout(() => {
      if (!isDeleting) {
        setDisplayedText(text.substring(0, index + 1));
        if (index + 1 === text.length) {
          setTimeout(() => setIsDeleting(true), pause); // pause before deleting
        }
        setIndex((prev) => prev + 1);
      } else {
        setDisplayedText(text.substring(0, index - 1));
        if (index - 1 === 0) {
          setIsDeleting(false);
        }
        setIndex((prev) => prev - 1);
      }
    }, speed);

    return () => clearTimeout(timeout);
  }, [displayedText, index, isDeleting, text, speed, pause]);

  return <h5>Hello, {displayedText}|</h5>; // "Hello," is static, cursor shown
};

export default TypingText;
