import React, { useState, useEffect } from "react";
import { FaDownload } from "react-icons/fa"; 
import "./App.css";
import TypingText from "./TypingText";
import oceanbg from './videos/oceanbg.mp4';

function App() {
  const [activeSection, setActiveSection] = useState("home");

  useEffect(() => {
  const sections = ["home", "about", "projects", "skills", "contact"];
  const observers = [];

  sections.forEach((id) => {
    const section = document.getElementById(id);
    if (!section) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setActiveSection(id);
          // Update the URL hash without scrolling
          window.history.replaceState(null, "", `#${id}`);
        }
      },
      {
        root: null,
        rootMargin: "-50% 0px -50% 0px",
        threshold: 0,
      }
    );

    observer.observe(section);
    observers.push(observer);
  });

  return () => {
    observers.forEach((observer) => observer.disconnect());
  };
}, []);

  return (
    <div>
      <nav>
        <div className="nav-left">Simon Tekle Tesfatsion</div>
        <div className="nav-links">
          <a href="#home" className={activeSection === "home" ? "active" : ""}>Home</a>
          <a href="#about" className={activeSection === "about" ? "active" : ""}>About</a>
          <a href="#projects" className={activeSection === "projects" ? "active" : ""}>Projects</a>
          <a href="#skills" className={activeSection === "skills" ? "active" : ""}>Skills</a>
          <a href="#contact" className={activeSection === "contact" ? "active" : ""}>Contact</a>
        </div>
      </nav>

      {/* Home Section */}
      <section className="section home-section" id="home">
        <video className="background-video" autoPlay loop muted playsInline>
          <source src={oceanbg} type="video/mp4" />
        </video>
        <div className="header caption">
          <TypingText texts={["Hello, I am Simon", "Welcome to my Portfolio"]} speed={90} pause={1500} />
          <p className="subtext">Software Developer • Creative Coder • Tech Enthusiast</p>
          <div className="button-group">
            <a href="/CV.pdf" download className="btn">
              <FaDownload style={{ marginRight: '8px', verticalAlign: 'middle'}} />
              Download CV
            </a>
            <a href="#contact" className="btn">
              Contact Me
            </a>
          </div>
        </div>
      </section>
      <section
  id="about"
  className="section space-section bg-[#0f0f1a] text-gray-300 py-16 relative"
>
  <div className="container mx-auto flex flex-col md:flex-row items-center justify-between gap-12 px-6 md:px-12 relative z-10">
    {/* Left: Profile Image */}
    <div className="w-full md:w-1/2 flex justify-center md:justify-end relative z-10">
      <img
        src="/Photo.png"
        alt="Simon Tekle Tesfatsion"
        className="w-64 h-64 md:w-80 md:h-80 rounded-full object-cover shadow-[0_0_40px_rgba(120,119,198,0.3)]"
      />
    </div>

    {/* Right: About Text */}
    <div className="w-full md:w-1/2 max-w-xl text-center md:text-left relative z-10">
      <h1 className="text-3xl font-bold mb-4 text-white">About Me</h1>
      <p className="mb-4">
        Hi, I’m <strong>Simon Tekle Tesfatsion</strong>, a recent Computer Science graduate from KTH.
        I’m passionate about software development, DevOps, and cloud technologies, and I love turning
        complex problems into elegant, working solutions.
      </p>
      <p>
        When I’m not coding, you’ll find me exploring finance charts, trading, or hitting the gym
        to debug my workout routine. I’m eager to learn, collaborate with great teams, and contribute
        to projects that make a real impact.
      </p>
    </div>
  </div>
</section>

      <section className="section space-section" id="projects">
        <div className="header caption"><h5>Projects</h5></div>
      </section>

      <section className="section space-section" id="skills">
        <div className="header caption"><h5>Skills</h5></div>
        
      </section>

      <section className="section space-section" id="contact">
        <div className="header caption"><h5>Contact</h5></div>
      </section>
    </div>
  );
}

export default App;
